mapper.select:
select b.bId, b.rtype2, b.descn descn2, b.reportid, a.* from vsa_imp_log a
  left join (select m.fid,n.id reportid, l.* from sa_file_rel l, sa_file_category m, sa_report_info n 
  where l.bid=m.id and n.fid=m.fid and l.btype=2 and m.type1='REPORT') b
on a.cateId=b.aId and b.rtype1=1 and b.rtype2='即时报告'

  
    
     and a.ownerId = #{ownerId,jdbcType=VARCHAR}
     and a.ownerType=#{ownerType,jdbcType=INTEGER}
     and a.cTime>= #{startTime,jdbcType=TIMESTAMP}
     and a.cTime < #{endTime,jdbcType=TIMESTAMP}
     and a.cFileName like CONCAT('%',#{searchStr,jdbcType=VARCHAR},'%')
  
     
order by a.cTime desc

select a.fid id, b.tablename tmpTableName, a.tmid,a.sheetName, b.ownerId, b.ownerType from sa_imp_tabmap_rel a, sa_md_tabmap_rel b

  
     and a.tmoid=b.id
     and b.ownerId = #{ownerId,jdbcType=VARCHAR}
     and b.ownerType=#{ownerType,jdbcType=INTEGER}
     and a.fid=#{fileId,jdbcType=VARCHAR}
  
     
order by sheetIndex

select  
from (sa_file_index a JOIN sa_file_category b)

  
    a.id=b.fid and b.type1='IMP'
     and a.ownerId = #{ownerId,jdbcType=VARCHAR}
     and a.ownerType=#{ownerType,jdbcType=INTEGER}
     and a.cTime>= #{startTime,jdbcType=TIMESTAMP}
     and a.cTime < #{endTime,jdbcType=TIMESTAMP}
     and b.extInfo like CONCAT('%',#{cFileName,jdbcType=VARCHAR},'%')
  
     
order by a.cTime desc
